const mysql = require('mysql2');
const databaseConfig = require('antinoPms_config').getDbConfig();


exports.executeQuery = async (query) =>{

try{
    const connection = mysql.createConnection(databaseConfig).promise();

    await connection.connect();

    const result = await connection.query(query);
      
    return result[0];
  
}
catch(err){
    console.log(err);
    throw err;
    
}


} 